import React, { useState, useEffect, useRef } from 'react';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Experience from './components/Experience';
import Portfolio from './components/Portfolio';
import Skills from './components/Skills';
import Footer from './components/Footer';
import AIAssistant from './components/AIAssistant';

const App: React.FC = () => {
  const [isDark, setIsDark] = useState(true);
  const [mousePos, setMousePos] = useState({ x: 0, y: 0 });
  const [cursorPos, setCursorPos] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);

  useEffect(() => {
    if (isDark) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [isDark]);

  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      setMousePos({ x: e.clientX, y: e.clientY });

      // Check if hovering over an interactive element
      const target = e.target as HTMLElement;
      const isInteractive = 
        target.tagName === 'A' || 
        target.tagName === 'BUTTON' || 
        target.closest('a') || 
        target.closest('button');

      setIsHovering(!!isInteractive);
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  // Smooth follow animation for the cursor
  useEffect(() => {
    let animationFrame: number;

    const followMouse = () => {
      setCursorPos(prev => ({
        x: prev.x + (mousePos.x - prev.x) * 0.15,
        y: prev.y + (mousePos.y - prev.y) * 0.15
      }));
      animationFrame = requestAnimationFrame(followMouse);
    };

    animationFrame = requestAnimationFrame(followMouse);
    return () => cancelAnimationFrame(animationFrame);
  }, [mousePos]);

  const toggleTheme = () => setIsDark(!isDark);

  return (
    <div className="min-h-screen transition-colors duration-300 relative">
      {/* Animated Background Spheres */}
      <div className="fixed inset-0 pointer-events-none -z-10 overflow-hidden bg-white dark:bg-[#050505]">
        <div className="absolute top-0 left-0 w-full h-full opacity-40 dark:opacity-60">
          <div className="absolute top-[10%] left-[15%] w-96 h-96 bg-indigo-900/40 dark:bg-indigo-950/40 rounded-full blur-[100px] animate-blob"></div>
          <div className="absolute bottom-[20%] right-[10%] w-[500px] h-[500px] bg-blue-900/30 dark:bg-blue-950/30 rounded-full blur-[120px] animate-blob-reverse"></div>
          <div className="absolute top-[40%] right-[30%] w-80 h-80 bg-slate-900/30 dark:bg-slate-900/50 rounded-full blur-[90px] animate-blob"></div>
          <div className="absolute bottom-[10%] left-[20%] w-[450px] h-[450px] bg-indigo-900/30 dark:bg-indigo-950/20 rounded-full blur-[110px] animate-blob-reverse"></div>
        </div>
      </div>

      {/* Custom Bubble Cursor */}
      <div 
        className={`fixed top-0 left-0 w-8 h-8 pointer-events-none z-[9999] transition-transform duration-300 ease-out border-2 border-indigo-500/50 rounded-full flex items-center justify-center mix-blend-difference ${isHovering ? 'scale-[2.5] bg-white/20' : 'scale-100'}`}
        style={{ transform: `translate(${cursorPos.x - 16}px, ${cursorPos.y - 16}px)` }}
      >
        <div className="w-1 h-1 bg-white rounded-full"></div>
      </div>

      <Navbar isDark={isDark} toggleTheme={toggleTheme} />
      <main className="relative">
        <Hero />
        <Experience />
        <Portfolio />
        <Skills />
      </main>
      <Footer />
      <AIAssistant />
    </div>
  );
};

